import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import './../App.css'
import TimerImg from './../timer.svg';
import HeartImg from './../heart.svg';
import SaveImg from './../save.svg';

function Instructions({show, onHide, sourceUrl, image, dishType="RECIPE", title, readyInMinutes, aggregateLikes, analyzedInstructions}) {
  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
      className="modal show"
      style={{borderRadius:0}}
      >
        <Modal.Header closeButton>
          <Modal.Title as="recipeTitle">{title}</Modal.Title>
        </Modal.Header>
        <img alt={title} src={image}/>
        <Modal.Body>
          {analyzedInstructions.map(inst => <li style={{margin:"10px"}}>{inst.number}. {inst.step}</li>)}
        </Modal.Body>
    </Modal>
  );
}

export default Instructions;
