import React, { useState, useEffect } from 'react';
import NavBar from './NavBar';
import SearchBar from './SearchBar'
import RecipeGrid from './RecipeGrid';
import axios from 'axios';
import './../App.css'

function SavedRecipes() {
    let [recipes, setRecipes] = useState()
    console.log("working")
    const token = localStorage.getItem("token");
    axios
    .get("http://localhost:4000/user/recipes", { headers: { token: token } })
    .then((res) => {
    setRecipes(res.data);
    console.log(recipes)
    console.log("recipes in SavedRecipes.jsx:");
    console.log(recipes);
    })
    .catch((error) => console.log(error));
    setRecipes(recipes.map(String).join(','))
    axios.get(`http://localhost:4000/saved-recipes?ingredients=${recipes}`, {timeout: 10 * 1000})
    .then((res) => {
        setRecipes(res.data)
        }, (err) => {
            console.log("Error: ", err);
    });

    return (
        <div style={{backgrondColor:"#FFFCF3"}}>
        <NavBar searchbar={false}/>
        {/* <RecipeGrid recipes={recipes}/> */}
        </div>
    );
}

export default SavedRecipes;
