import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import './../App.css';
import TimerImg from './../timer.svg';
import HeartImg from './../heart.svg';
import SaveImg from './../save.svg';

function RecipeOTD({sourceUrl, image, summary, title, readyInMinutes, aggregateLikes, }) {
    return (

      <Card as='RecipeOTD' style={{ width: "700px", height: "850px", border:0, borderRadius:0, backgroundColor:'#FFFCF3'}}>
        <Card.Img variant="top" alt={title} src={image} style={{borderRadius:0, padding:"50px", paddingBottom:15}}/> {/* Replace with "image" from api */}
        <Card.Body>
          <Card.Subtitle id="center" as="subtext" margin= "10px">RECIPE OF THE DAY</Card.Subtitle> {/* Replace with "dishType" from api */}
          <div></div>
          <Card.Title id="center" as="recipeTitleOTD" margin= "10px">{title}</Card.Title> {/* Replace with "title" from api */}
          <Card.Text id="center"></Card.Text>
          <Container fluid>
            <Row id="bottomBarOTD">
              <Col id="bottomBarLeft">
                <img alt="timer svg" src={TimerImg} width="20" verticalAlign="middle"/>
                {readyInMinutes} mins 
              </Col>
              <Col lg={6}><a id="linkOTD" href={sourceUrl}>GET THE RECIPE {'>'}</a></Col>
              <Col id="bottomBarRight">
                <img alt="heart svg" src={HeartImg} width="20"/>
                {aggregateLikes}
                <img alt="bookmark svg" src={SaveImg} height="20"/>
              </Col>
            </Row>
          </Container>
        </Card.Body>
      </Card>

    );
  }

export default RecipeOTD;
