import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import './../App.css'

function Instructions({show, onHide, sourceUrl, image, dishType="RECIPE", title, readyInMinutes, aggregateLikes, analyzedInstructions}) {
  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
      className="modal show"
      style={{borderRadius:0}}
      >
        <Modal.Header closeButton>
          <Modal.Title as="recipeTitle">{title}</Modal.Title>
        </Modal.Header>
        <img alt={title} src={image}/>
        <Modal.Body>
          {analyzedInstructions.map(inst => <div>{inst.number}. {inst.step}</div>)}
        </Modal.Body>

        <Modal.Footer>
        <Container id="bottomBar" fluid>
              <Row>
                <Col id="bottomBarLeft" style={{ display: 'flex',
          alignItems: 'center', justifyContent: 'left', marginLeft: 0,}}>
                  <img id="svg" alt="timer svg" src={TimerImg} width="20" verticalAlign="middle"/>
                  {readyInMinutes} mins 
                </Col>
                <Col id="bottomBarRight" style={{ display: 'flex',
          alignItems: 'center', justifyContent: 'right', }}>
                  <img id="svg" alt="heart svg" src={HeartImg} width="22"/>
                  {aggregateLikes}
                  <img id="svg" alt="bookmark svg" src={SaveImg} height="18"/>
                </Col>
              </Row>
            </Container>
          {/* <Button onClick={onHide} variant="secondary">Close</Button> */}
        </Modal.Footer>
    </Modal>
  );
}

export default Instructions;
