import React from 'react';
import axios from 'axios';
import RecipeCard from './RecipeCard';
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import './../App.css';

function RecipeGrid() {
  const [recipes, setRecipes] = useState("");
  axios.get(`http://localhost:4000/searched-recipes`, {timeout: 10 * 1000}).then((body) => {
        setRecipes(body.data);
        console.log(body.data);
    }, (err) => {
        console.log("Error: ", err);
        document.getElementById("recipes").innerHTML = "Error!";
        document.getElementById("load5").disabled = false;
    });

    return (
        <Container fluid="md">
        <Row md="4">
        <>
        {randomRecipes.recipes.map((r) => (
          <Col>
          <RecipeCard 
            sourceUrl={r.sourceUrl}
            image={r.image} 
            dishType={r.dishTypes[0] && r.dishTypes[0].toUpperCase()} 
            title={r.title}
            readyInMinutes={r.readyInMinutes}
            aggregateLikes={r.aggregateLikes}
            analyzedInstructions={r.analyzedInstructions[0].steps}
            extendedIngredients={r.extendedIngredients}
            >
          </RecipeCard>
          </Col>
        ))}
      </>
      </Row>
      </Container>
    );
  }

export default RecipeGrid;
