import React from 'react';
import './../App.css'
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function SearchBar() {
  const load_recipe = () => {
    preventDefault();
    let ingredients = document.getElementById("ourInput").value;
    document.getElementById("header").innerHTML = "Loading...";
    document.getElementById("img").src = "";
    document.getElementById("load").disabled = true;

    ingredients = ingredients.replaceAll(" ", "");
    ingredients = ingredients.replaceAll(",", ",+");
    axios.get(`http://localhost:4000/searched-recipes?ingredients=${ingredients}`, {timeout: 10 * 1000}).then((body) => {
        console.log("Received response from server for Q1: ", body.data);
        document.getElementById("header").innerHTML = `${body.data.name} (${body.data.date})`;
        document.getElementById("img").src = body.data.url;
        document.getElementById("load").disabled = false;
    }, (err) => {
        console.log("Error: ", err);
        document.getElementById("header").innerHTML = "Error!";
        document.getElementById("load").disabled = false;
    });
}
    return (
        <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Search Bar</Form.Label>
          <Form.Control type="text" placeholder="Feed us your ingredients..." />
        </Form.Group>
        <Button variant="primary" type="submit" onClick={load_recipe}>
          Submit
        </Button>
      </Form>
    );
  }

export default SearchBar;
