import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

function Instructions({sourceUrl, image, dishType="RECIPE", title, readyInMinutes, aggregateLikes, analyzedInstructions}) {
  return (
    <div
      className="modal show"
      style={{ display: 'block', position: 'initial' }}
    >
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Img alt={title} src={image} style={{borderRadius:0}}/>
        <Modal.Body>
          {analyzedInstructions.map(inst => <div>{inst.number}. {inst.step}</div>)}
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary">Close</Button>
          <Button variant="primary">Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  );
}

export default Instructions;
