import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import './../App.css';
import TimerImg from './../timer.svg';
import HeartImg from './../heart.svg';
import SaveImg from './../save.svg';

function RecipeOTD({sourceUrl, image, summary, dishType="RECIPE", title, readyInMinutes, aggregateLikes, }) {
    return (
      <a id='RecipeCardLink' href={sourceUrl} style={{textDecoration: "none", color: "inherit"}}>
        <Card as='RecipeOTD' style={{ width: "600px", height: "700px", borderRadius:0}}>
          <Card.Img variant="top" alt={title} src={image} style={{borderRadius:0, padding:50}}/> {/* Replace with "image" from api */}
          <Card.Body>
            <Card.Subtitle id="center" as="subtext" margin= "10px">{dishType}</Card.Subtitle> {/* Replace with "dishType" from api */}
            <div></div>
            <Card.Title id="center" as="recipeTitle" margin= "10px">{title}</Card.Title> {/* Replace with "title" from api */}
            <Card.Text id="center">If you want to add more <b>lacto ovo vegetarian</b> recipes to your recipe box, Rustic Brie Toasts with Wild Mushroom, Cranberry and Shallot might be a recipe you should try. This recipe makes 10 servings with <b>159 calories</b>, <b>8g of protein</b>, and <b>8g of fat</b> each. For <b>66 cents per serving</b>, this recipe <b>covers 6%</b> of your daily requirements of vitamins and minerals. 18 people were glad they tried this recipe. A mixture of salt and pepper, wedge of beautiful brie cheese, 1/4 cup dried cranberry (juice sweetened if possible), and a handful of other ingredients are all it takes to make this recipe so tasty. From preparation to the plate, this recipe takes approximately <b>45 minutes</b>. It is brought to you by Foodista. Overall, this recipe earns a <b>solid spoonacular score of 48%</b>. If you like this recipe, you might also like recipes such as <a href="https://spoonacular.com/recipes/brie-toasts-with-cranberry-compote-430592">Brie Toasts with Cranberry Compote</a>, <a href="https://spoonacular.com/recipes/brown-sugar-cured-turkey-with-wild-mushroom-shallot-gravy-116265">Brown Sugar-Cured Turkey with Wild Mushroom-Shallot Gravy</a>, and <a href="https://spoonacular.com/recipes/roast-beef-with-wild-mushroom-sauce-and-caramelized-shallot-mashed-potatoes-214219">Roast Beef with Wild Mushroom Sauce and Caramelized-Shallot Mashed Potatoes</a>.</Card.Text>
            <Container fluid  id="center">
              <Row id="bottomBar">
                <Col id="bottomBarLeft">
                  <img alt="timer svg" src={TimerImg} width="20" verticalAlign="middle"/>
                  {readyInMinutes} mins 
                </Col>
                <Col id="bottomBarRight">
                  <img alt="heart svg" src={HeartImg} width="20"/>
                  {aggregateLikes}
                  <img alt="bookmark svg" src={SaveImg} height="20"/>
                </Col>
              </Row>
            </Container>
          </Card.Body>
        </Card>
      </a>
    );
  }

export default RecipeOTD;
