import React from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './../App.css';


function RecipeCard({image, dishType, title, readyInMinutes, aggregateLikes}) {
    return (
      <Card as='RecipeCard' id="recipeCard" style={{ width: "300px", height: "400px", borderRadius:0}}>
        <img variant="top" src={image} /> {/* Replace with "image" from api */}
        <Card.Body>
          <Card.Subtitle as="subtext" style={{ padding: "10px" }}>{dishType}</Card.Subtitle> {/* Replace with "dishType" from api */}
          <div></div>
          <Card.Title as="recipeTitle" style={{ paddingAll: "10px" }}>{title}</Card.Title> {/* Replace with "title" from api */}
        </Card.Body>
      </Card>
    );
  }

export default RecipeCard;
