import React, { useState } from "react";
import './../App.css'
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import RecipeGrid from './RecipeGrid'


function SearchBar(version) {
    let [recipes, setRecipes] = useState()
    const load_recipe = (e) => {
      e.preventDefault();
      let ingredients = e.target[0].value;
      ingredients = ingredients.replaceAll(" ", "");
      ingredients = ingredients.replaceAll(",", ",+");  
      axios.get(`http://localhost:4000/searched-recipes?ingredients=${ingredients}`, {timeout: 10 * 1000})
      .then((res) => {
        setRecipes(res.data)
      }, (err) => {
          console.log("Error: ", err);
    });
}
  if (version === "SearchPage") {
    return (
      <>
        <Form id="center" onSubmit={load_recipe}>
        <Form.Group className="mb-3">
          <Form.Control
          id="searchBar" 
          type="text" 
          placeholder="Feed us your ingredients..." 
          />
        </Form.Group>
      </Form>
      <RecipeGrid recipes={recipes}></RecipeGrid>
      </>
    );
  }
  else if (version === "ResultPage") {
    return (
      <>
        <Form id="center" onSubmit={load_recipe}>
        <Form.Group className="mb-3">
          <Form.Control
          id="searchBar" 
          type="text" 
          placeholder="Feed us your ingredients..." 
          />
        </Form.Group>
      </Form>
      <RecipeGrid recipes={recipes}></RecipeGrid>
      </>
    );
  }
}


export default SearchBar;
