import React, { useState } from "react";
import axios from "axios";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import './../App.css';

export const Login = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:4000/user/login", {
        email: email,
        password: password,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <Card as='LoginCard' style={{ width: "600px", height: "700px", borderRadius:0}}>
      <Form width="500" height="500" onSubmit={handleSubmit}>
        <h1>Foodstagram</h1>
        <h2>Welcom to Foodstagram</h2>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label htmlFor="email">Email address</Form.Label>
          <Form.Control 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            type="email"
            // placeholder="youremail@gmail.com"
            id="email"
            name="email"
            style={{width: "300px", borderRadius: 0, border: "0px", borderBottom: "1px solid black"}}
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label htmlFor="password">Password</Form.Label>
          <Form.Control 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            // placeholder="********"
            id="password"
            name="password"
            style={{width: "300px", borderRadius: 0, border: "0px", borderBottom: "1px solid black"}}
          />
        </Form.Group> 
        <Button variant="primary" type="submit">
          Log In
        </Button>
      {/* <form className="login-form" onSubmit={handleSubmit}>
        <label htmlFor="email">email</label>
        <input
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="email"
          placeholder="youremail@gmail.com"
          id="email"
          name="email"
        />
        <label htmlFor="password">password</label>
        <input
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          placeholder="********"
          id="password"
          name="password"
        />
        <button type="submit">Log In</button>
      </form> */}
      <div></div>
      <a>
        New to Foodstagram?
      </a>
      <Button 
        variant="link"
        className="link-btn"
        onClick={() => props.onFormSwitch("register")}
      >
        Create Account
      </Button>
    </Form>
    </Card>
  );
};
