import React, { useState } from 'react';
import NavBar from './NavBar';
import SearchBar from './SearchBar'
import RecipeGrid from './RecipeGrid';
import axios from 'axios';
import './../App.css'

function ResultPage() {
    let [recipes, setRecipes] = useState()
    const load_recipe = (e) => {
        let savedRecipes 
        const token = localStorage.getItem("token");
        axios
        .get("http://localhost:4000/user/recipes", { headers: { token: token } })
        .then((res) => {
        savedRecipes = res;
        console.log("recipes in SavedRecipes.jsx:");
        console.log(savedRecipes);
        })
        .catch((error) => console.log(error));
        savedRecipes = savedRecipes.map(String).join(',')
        axios.get(`http://localhost:4000/searched-recipes?ingredients=${savedRecipes}`, {timeout: 10 * 1000})
        .then((res) => {
            setRecipes(res.data)
          }, (err) => {
              console.log("Error: ", err);
        });

    }
    return (
        <div onLoad={() => load_recipe} style={{backgrondColor:"#FFFCF3"}}>
        <NavBar searchbar={false}/>
        <RecipeGrid recipes={recipes}/>
        </div>
    );
}

export default ResultPage;
